        static void Main(string[] args)
        {

            ProblemOne(input);
            Console.ReadKey();
        }


        static void ProblemOne(string input)
        {
            int floor = 0;
            int position = 1;
            foreach (char c in input)
            {
                if (c == '(')
                {
                    floor++;
                }
                else
                {
                    floor--;
                }

                if (floor == -1)
                {
                    Console.WriteLine(floor + " " + position);
                    return;
                }

                position++;
            }
            Console.WriteLine("Didn't reach basement");
        }

