        public int Day => 1;
        public int Year => 2015;

        public void ProblemOne()
        {
            int floor = 0;
            foreach (char c in input)
            {
                if (c == '(')
                {
                    floor++;
                }
                else
                {
                    floor--;
                }
            }
            Console.WriteLine(floor);
        }

        public void ProblemTwo()
        {
            int floor = 0;
            int position = 1;
            foreach (char c in input)
            {
                if (c == '(')
                {
                    floor++;
                }
                else
                {
                    floor--;
                }

                if (floor == -1)
                {
                    Console.WriteLine(floor + " " + position);
                    return;
                }

                position++;
            }
            Console.WriteLine("Didn't reach basement");
        }

