        private char[,] ParseInput(string input)
        {
            var lines = input.SplitNewLine();
            var array = new char[lines[0].Length, lines.Length];

            for (int y = 0; y < array.GetLength(1); y++)
            {
                for (int x = 0; x < array.GetLength(0); x++)
                {
                    array[x, y] = lines[y][x];
                }
            }

            return array;
        }

