void p_2015_day01_01()
{
    int floor = 0;
    int len = strlen(input);
    for(int i = 0; i < len; i++)
    {
        if(input[i] == '(')
        {
            floor++;
        }
        else
        {
            floor--;
        }
    }
    printf("floor: %i", floor);
}

void p_2015_day01_02()
{
    int floor = 0;
    int len = strlen(input);
    for(int i = 0; i < len; i++)
    {
        if(input[i] == '(')
        {
            floor++;
        }
        else
        {
            floor--;
        }

        if(floor == -1)
        {
            printf("position: %i", i+1);
            return;
        }
    }

}